mapper.select:
select a.id, a.deptid as "dept.id", b.name as "dept.name", a.teacherid as "user.userId", c.realName as "user.realName", a.islead, a.courseid as "course.id", d.name as "course.name" from classteacher a left join dept b on a.deptid = b.id left join user c on a.teacherid = c.userid left join course d on a.courseid = d.id where 1 = 1
and a.deptid = #{dept.id}
and a.teacherid = #{user.userId}
and a.courseid = #{course.id}
and (b.name like '%' #{start} '%' or c.realName like '%' #{start} '%' or d.name like '%' #{start} '%' )
limit #{page},#{rows}

select 
	count(*)
from classteacher a left join dept b on a.deptid = b.id
left join user c on a.teacherid = c.userid 
left join course d on a.courseid = d.id
 where 1 = 1	

	and a.deptid = #{dept.id}


	and a.teacherid = #{user.userId}


	and a.courseid = #{course.id}


	and (b.name like   '%' #{start} '%'   or c.realName like   '%' #{start} '%'  or  d.name  like   '%' #{start} '%'   )

mapper.update:
update classteacher set
islead=#{islead}, deptId=#{dept.id}, courseid=#{course.id}, teacherId=#{user.userId},
id=#{id}
