mapper.select:
select a.id as "id", a.date as "date", a.result as "result", a.periodid as "period.id", e.name as "period.name", a.courseid as "course.id", f.name as "course.name", a.teacherid as "teacher.userId", g.realName as "teacher.realName", a.studentid as "student.userId", b.realName as "student.realName", c.id as "dept.id",c.name as "dept.name", d.id as "grade.id",d.name as "grade.name" from att a left join user b on a.studentid = b.userId left join dept c on b.deptid = c.id left join dept d on c.pid = d.id left join period e on a.periodid = e.id left join course f on a.courseid = f.id left join user g on a.teacherid = g.userid where 1 = 1
and b.deptid = #{dept.id}
and d.id = #{grade.id}
and a.studentid = #{student.userId}
and result = #{result}
and (b.realName like '%' #{keyword} '%' or g.realName like '%' #{keyword} '%')
and a.date >= #{start}
and a.date <= #{end}
order by a.date desc
limit #{page},#{rows}

select 
	count(*)
from att a 
left join user b on a.studentid = b.userId
left join dept c on b.deptid = c.id
left join dept d on c.pid = d.id
left join period e on a.periodid = e.id
left join course f on a.courseid = f.id
left join user g on a.teacherid = g.userid where 1 = 1 

	and b.deptid = #{dept.id}


	and d.id = #{grade.id}


	and result = #{result}


	and a.studentid = #{student.userId}


	and (b.realName like  '%' #{keyword} '%' or g.realName like  '%' #{keyword} '%') 


	and a.date >= #{start}



	and a.date <= #{end}

SELECT  
	t.studentid as "student.userId",
	b.realName as "student.realName",
	t.total as "totalcount",
	c.id as "dept.id",c.name as "dept.name",
	d.id as "grade.id",d.name as "grade.name"
	FROM (
		SELECT a.studentid,COUNT(a.studentid) AS total
		FROM att a WHERE result = #{result}
		
			 AND a.DATE>= #{start}
		
		
			and a.date <= #{end}
		
		GROUP BY a.studentid  HAVING total >= #{total}
	) t LEFT JOIN USER b ON t.studentid = b.userId
	left join dept c on b.deptid = c.id
	left join dept d on c.pid = d.id
	
		where b.realName like '%' #{keyword} '%'
	
	order by total desc 
	
		limit #{page},#{rows}

SELECT  
count(*)
	FROM (
		SELECT a.studentid,COUNT(a.studentid) AS total
		FROM att a WHERE result = #{result}
		
			 AND a.DATE>= #{start}
		
		
			and a.date <= #{end}
		
		GROUP BY a.studentid  HAVING total >= #{total}
	) t LEFT JOIN USER b ON t.studentid = b.userId
	left join dept c on b.deptid = c.id
	left join dept d on c.pid = d.id
	
		where b.realName like '%' #{keyword} '%'
