mapper.select:
select a.id, a.deptid as "dept.id", b.name as "dept.name", a.periodid as "period.id", c.name as "period.name", a.week as "week", a.ctid as "ct.id", d.teacherid as "ct.user.userId", e.realName as "ct.user.realName", d.courseid as "ct.course.id", f.name as "ct.course.name" from schedular a left join dept b on a.deptid = b.id left join period c on a.periodid = c.id left join classteacher d on a.ctid = d.id left join user e on d.teacherid = e.userid left join course f on d.courseid = f.id where 1 = 1
and a.deptid = #{dept.id}
and a.week = #{week}
and d.teacherid = #{ct.user.userId}
and a.periodid = #{period.id}
and d.courseid = #{ct.course.id}
order by a.week
limit #{page},#{rows}

select 
	count(*)
from schedular a left join dept b on a.deptid = b.id 
left  join period c on a.periodid = c.id
left  join classteacher d on a.ctid = d.id
left  join user e on d.teacherid  = e.userid
left  join course f on d.courseid = f.id
where 1 = 1	

	and a.deptid = #{dept.id}


	and d.teacherid = #{ct.user.userId}


	and d.courseid = #{ct.course.id}


	and a.week = #{week}


	and a.periodid = #{period.id}

mapper.update:
update schedular set
week=#{week}, deptId=#{dept.id}, periodid=#{period.id}, ctid=#{ct.id},
id=#{id}
